require 'schema'
require 'basis_processing'
require 'distributions'

class MySketch < Processing::App
	app = self
	
	def mean(responses, &metric)
		mean = 0.0
		responses.each {|r| mean += metric.call(r)}
		mean /= responses.count
		mean
	end
	
	def variance(samples, mean_x, mean_y, x_metric, y_metric)
		sum = 0.0
		samples.each {|s| sum += (x_metric.call(s) - mean_x)*(y_metric.call(s) - mean_y)}
		sum/samples.count
	end

	def setup
		@screen_height = 950
		@width = width
		@height = height
		@screen_transform = Transform.new({:x => 10.0, :y => -15.0}, {:x => 500, :y => @screen_height})
		smooth
		no_loop
		background(0,0,0)
		color_mode(HSB, 1.0)

		x = data
		sorted_clusters = x.keys.sort
		@c = CoordinateSystem.standard({:minimum => 0, :maximum => 60}, {:minimum => 0, :maximum => sorted_clusters.count - 1}, self, {:x => "Question number", :y => "Cluster"})
		@screen = Screen.new(@screen_transform, self, @c)
		rect_mode(CENTER)
		fill(0.3,1,1)
		sorted_clusters.each_index do |cluster_index|
			stats = x[sorted_clusters[cluster_index]]
			stats.each_index do |stat_index|
				@screen.plot({ :x => stat_index, :y => cluster_index}, :track => true) if stats[stat_index] == 0
			end
		end
		@screen.draw_axes(10,1, {:x => ->(x){x}, :y => ->(y){"#{sorted_clusters[y][0..4]}"}})
		@highlight_block = ->(o,m,s) do
			rect_mode(CENTER)
			rect(m[:x], m[:y], 8, 8)
			text("(#{o[:x]}, #{o[:y]}) -> #{sorted_clusters[o[:y]]}", m[:x] + 5, m[:y] + 5)
		end
	end

	def draw
	end
end

w = 1200
h = 1000

MySketch.send :include, Interactive
MySketch.new(:title => "My Sketch", :width => w, :height => h)



